package basic;
import java.util.*;
public class swapping1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a,b,c;
		Scanner sc=new  Scanner(System.in);
		System.out.println("Enter value of a & b");
		a=sc.nextInt();
		b=sc.nextInt();
		c=a; 
		a=b; 
		b=c; 
		System.out.println("a= "+a+"  b= "+b);}

}
