package cconversion;
import java.util.*;

public class int_to_string {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int i;
        Scanner sc=new Scanner(System.in);
        System.out.println("enter value");
        i=sc.nextInt();
        String s=String.valueOf(i);
        System.out.println(s);
	}

}
