package forloop;
import java.util.*;
public class sumuptonnumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int i,n,sum=0;
      Scanner sc=new Scanner(System.in);
      System.out.println("enter the number");
      n=sc.nextInt();
      for(i=1;i<=n;i++){
    	  sum=sum+i;
      
    	  System.out.println(sum);
      }  
      }
      
	

}
