package pattern;

import java.util.Scanner;

public class pattern9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n,i,j;
	     Scanner sc=new Scanner(System.in);
		System.out.println("enter number");
		n=sc.nextInt();
		for(i=1;i<=n;i++){
			for (j=1;j<=i;j++){
				System.out.print(""+j);
			}
		
		System.out.println();
}
	
}


}
